package com.example.RainforestRetail.controllers;import com.example.RainforestRetail.models.User;import com.example.RainforestRetail.services.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/users")public class UserController {    @Autowired    UserService userService;    @PostMapping    public ResponseEntity<User> addNewUser(@RequestBody User user ){        userService.createNewUser(user);        return new ResponseEntity<>(user, HttpStatus.CREATED);    }}