package com.example.RainforestRetail.services;import com.example.RainforestRetail.models.Order;import com.example.RainforestRetail.models.User;import com.example.RainforestRetail.repositories.OrderRepository;import com.example.RainforestRetail.repositories.ProductOrderRepository;import com.example.RainforestRetail.repositories.ProductRepository;import com.example.RainforestRetail.repositories.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.time.LocalDateTime;import java.util.Optional;@Servicepublic class OrderService {    @Autowired    UserRepository userRepository;    @Autowired    OrderRepository orderRepository;    @Autowired    ProductOrderRepository productOrderRepository;    @Autowired    ProductRepository productRepository;    public Order createOrder(long id) {        Optional<User> user = userRepository.findById(id);        if (user.isPresent()) {            Order order = new Order(user.get(), LocalDateTime.now());            orderRepository.save(order);            return order;        }        return null;    }}